document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelectorAll(".scroll-effect"),t=()=>{const t=window.pageYOffset||document.documentElement.scrollTop,n=window.innerHeight;e.forEach((e=>{const o=e.getBoundingClientRect().top+t;t+n>o+50?e.classList.add("visible"):e.classList.remove("visible")}))};window.addEventListener("scroll",t),t()}));